package coreJava;

public class UniaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 5;
		System.out.println(x);//5
		int y = x++;
		System.out.println(x);//6
		System.out.println(y);//5
		System.out.println(x);//6
		System.out.println(y);//5
	}

}
